/**
 * kUtilities jQuery Plugin - kFacebookBtn 
 * --------------------------------------------------------------------------
 *
 * Plugin: Turn an <A> tag into Facebook like button
 * 
 * 
 * @version     0.1
 * @since       29 Jun 2011
 * @author      _ken
 * 
 */

;(function($){$.fn.kFacebookBtn=function(method){var defaults={fbLike:'http://www.facebook.com/plugins/like.php',layout:'standard',width:450,height:90,showFaces:true,verb:'like',colorScheme:'light',font:'',locale:'en_US'};var plugin=this;plugin.o={};var methods={init:function(options){plugin.o=$.extend({},defaults,options);return plugin.each(function(){var $element=$(this),element=this;if(!($element.is('a')))return;var src,url,layout,width,height,showFaces,verb,colorScheme,font;url=$element.attr('href');layout=plugin.o.layout;width=plugin.o.width;height=plugin.o.height;showFaces=plugin.o.showFaces;colorScheme=plugin.o.colorScheme;font=plugin.o.font;verb=$element.text().toLowerCase();locale=plugin.o.locale;if(verb!='like'&&verb!='recommend')verb=plugin.o.verb;var $iframe=$('<iframe scrolling="no" frameborder="0" allowTransparency="true">');src=plugin.o.fbLike+'?href='+url+'&send=false&amp;layout='+layout+'&width='+width+'&show_faces='+showFaces+'&action='+verb+'&colorscheme='+colorScheme+'&font='+font+'&locale='+locale+'&height='+height;$iframe.attr('src',src).css({'border':'none','overflow':'hidden','width':width,'height':height}).insertAfter($element);$element.hide()})}};if(methods[method]){return methods[method].apply(this,Array.prototype.slice.call(arguments,1))}else if(typeof method==='object'||!method){return methods.init.apply(this,arguments)}else{$.error('Method '+method+' does not exist.')}}})(jQuery);